const items = $input.all();
if (items.length === 0) return [];

const body = items[0].json.body['airscale-data'];

function formatDate(dateString) {
  if (!dateString) return null;
  const d = new Date(dateString);
  if (isNaN(d)) return null;
  return `${d.getUTCFullYear()}-${String(d.getUTCMonth() + 1).padStart(2, '0')}`;
}

const positions = (body.positionGroups?.contents || []).flatMap(group =>
  (group.profilePositions || []).map(pos => ({
    company: pos.company || null,
    title: pos.title || null,
    description: pos.description || null,
    startDate: formatDate(pos.date?.start),
    employmentType: pos.employmentType || null,
    location: pos.location || null,
  }))
);

const educations = (body.educations?.contents || []).map(edu => ({
  school: edu.school?.name || null,
  degree: edu.degreeName || null,
  fieldOfStudy: edu.fieldOfStudy || null,
  startDate: edu.date?.start ? String(new Date(edu.date.start).getUTCFullYear()) : null,
  endDate: edu.date?.end ? String(new Date(edu.date.end).getUTCFullYear()) : null,
}));

const certifications = (body.certifications?.contents || []).map(cert => ({
  name: cert.name || null,
  authority: cert.authority || null,
  startDate: formatDate(cert.date?.start) || null,
}));

const projects = (body.projects?.contents || []).map(proj => ({
  title: proj.title || null,
  startDate: formatDate(proj.date?.start) || null,
  endDate: formatDate(proj.date?.end) || null,
}));

const courses = (body.courses?.contents || []).map(course => ({
  name: course.name || null,

}));

const awards = (body.awards?.contents || []).map(award => ({
  name: award.name || null,
  issuer: award.issuer || null,    // Adjust field names if needed
  date: formatDate(award.date) || null,
}));

const languages = (body.languages?.contents || []).map(lang => ({
  name: lang.name || null,

}));

const output = {
  firstName: body.firstname || null,
  lastName: body.lastname || null,
  headline: body.headline || null,
  summary: body.summary || null,
  location: body.location?.defaultValue || null,
  positions,
  educations,
  certifications,
  projects,
  courses,
  awards,
  languages,
  skills: body.skills?.contents || [],
};

return [
  { json: output }
];

const keys = Object.keys(items[0].json);
if (!keys.length) throw new Error("Input JSON has no keys");

const firstKey = keys[0];
const structure = items[0].json[firstKey]?.structure;

if (!structure) {
  throw new Error("Structure not found");
}

// Extract IDs as numbers into an array
const allIDs = structure.map(item => Number(item.ID));

// Return the entire IDs array inside one object (one item)
return [{ json: { allIDs } }];

const allIDs = items[0].json.allIDs;
const data = items[0].json[""];

if (!Array.isArray(allIDs)) {
  throw new Error("allIDs is not an array");
}
if (!Array.isArray(data)) {
  throw new Error("Data array not found");
}

// Filter data to only include items with ID in allIDs
let filteredSections = data.filter(item => allIDs.includes(item.ID));

// Sort filtered sections by ascending ID number
filteredSections.sort((a, b) => a.ID - b.ID);

// Return as one item containing filtered and sorted sections array
return [{ json: { filteredSections } }];

