// Initialize an array for the output with objects containing URL as key and markdown as value
const markdownArray = [];

// Loop over each input item
for (const item of items) {
  if (item.json && Array.isArray(item.json.data)) {
    for (const entry of item.json.data) {
      if (entry.markdown && entry.metadata && entry.metadata.url) {
        // Push an object with URL as key and markdown string as value
        const obj = {};
        obj[entry.metadata.url] = entry.markdown;
        markdownArray.push(obj);
      }
    }
  }
}

// Return an array with one item, containing the array of markdown objects under key 'markdown'
return [{ json: { markdown: markdownArray } }];

